import React from "react"
class Events extends React.Component {
    constructor() {
        super()
        this.state = {
            num: 0
        }
        this.AddCount = this.AddCount.bind(this)
    }
    AddCount() {
        this.setState(prevState => {
            num = prevState.num + 2
        })
    }
    render() {
        return (
            <div>
                <p>{this.state.num}</p>
                <button onClick={this.AddCount} className="button">Add</button>
            </div>
        )
    }
}
export default Events
